// --- Extract from Instagram webhook (DM) ---
const msg = $('Webhook').item.json.body.entry?.[0]?.messaging?.[0] || {};
const senderId = msg.sender?.id || msg.from?.id || "";
const text = (msg.message?.text || msg.message?.message || msg.postback?.payload || "").trim();

// Basic language guess (very naive; replace later with a proper detector if you want)
const guessLang = (t) => /[ƒÖƒáƒô≈Ç≈Ñ√≥≈õ≈º≈∫]/i.test(t) ? "pl" : "en";

// Build base object carried through the flow
return [{
  json: {
    ig_user_id: senderId,
    message_text: text,
    message_ts: new Date().toISOString(),
    locale: guessLang(text),
    // Placeholders to be filled later
    name: "",
    email: "",
    phone: "",
    service: "",
    action: "",                // booking | reschedule | cancel | check availability | info
    date_phrase: "",           // e.g., "this Friday 18:00"
    weekday: "",               // mon..sun
    week_hint: "none",         // this|next|none
    time_24h: "",              // HH:MM
    new_date_phrase: "",
    new_weekday: "",
    new_week_hint: "none",
    new_time_24h: ""
  }
}];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

