// Takes LLM output from the previous node (as text or object) and normalizes.
// Also merges with the fields produced by "Extract Message Data" (via pairedItem).

const raw = typeof $json === 'string' ? $json : ($json.text ?? JSON.stringify($json));
const base = $items('Extract Message Data', 0, 0).json;

function safeParse(s) {
  const m = (s || "").toString().match(/\{[\s\S]*\}/);
  if (!m) return {};
  try { return JSON.parse(m[0]); } catch { return {}; }
}

function get(v){ return (v ?? "").toString().trim(); }

const ai = safeParse(raw);

// Merge with defaults from base
const out = {
  ...base,
  name: get(ai.name || base.name),
  email: get(ai.email || base.email),
  phone: get(ai.phone || base.phone),
  service: get(ai.service || base.service),
  action: get(ai.action || ai.intent || base.action),

  // time extraction (booking/reschedule/availability)
  date_phrase: get(ai.date_phrase || base.date_phrase),
  weekday: get(ai.weekday || base.weekday),
  week_hint: get(ai.week_hint || base.week_hint || "none"),
  time_24h: get(ai.time_24h || ai.time || base.time_24h),

  new_date_phrase: get(ai.new_date_phrase || base.new_date_phrase),
  new_weekday: get(ai.new_weekday || base.new_weekday),
  new_week_hint: get(ai.new_week_hint || base.new_week_hint || "none"),
  new_time_24h: get(ai.new_time_24h || base.new_time_24h),

  // fallback when model returns a single "booking_time"/"reschedule_time"
  booking_time: get(ai.booking_time || ""),
  reschedule_time: get(ai.reschedule_time || ""),
  availability_time: get(ai.availability_time || "")
};

// quick hygiene
function cleanPhone(p){ const s=(p||"").replace(/[^\d+]/g,""); return s.length>=7 ? s : ""; }
function cleanEmail(e){ return /.+@.+\..+/.test(e||"") ? e : ""; }
out.phone = cleanPhone(out.phone);
out.email = cleanEmail(out.email);

// normalize action to 4 canonical values
const map = { book:'booking', booking:'booking', reserve:'booking', reschedule:'reschedule', move:'reschedule', cancel:'cancel', availability:'check availability', 'check availability':'check availability' };
out.action = map[out.action?.toLowerCase()] || out.action?.toLowerCase() || "info";

return [{ json: out }];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

